    ],
    "DataModels": [
      "**VinylRecord**: { \u0027id\u0027: \u0027string\u0027, \u0027title\u0027: \u0027string\u0027, \u0027artist\u0027: \u0027string\u0027, \u0027genre\u0027: \u0027string\u0027, \u0027release_year\u0027: \u0027number\u0027, \u0027condition\u0027: \u0027string\u0027, \u0027notes\u0027: \u0027string\u0027, \u0027cover_image_url\u0027: \u0027string\u0027 }",
